mapper.insert:
		INSERT INTO USER_TBL(
                           NUM,   ID,    PW,       NAME,     GENDER,
                           EMAIL, PHONE, BIRTHDAY, JOINDATE, ENABLED
	                       )
	                VALUES(
	   	  	               USER_NUM_SEQ.NEXTVAL, #{id},    #{pw},       #{name},  #{gender},
	   	  	               #{email},             #{phone}, #{birthday}, SYSDATE,  #{enabled}
	                       )

mapper.select:
	SELECT COUNT(*)
  	  FROM (SELECT ${fld}
  			  FROM USER_TBL
  			 WHERE ID != #{id})
	 WHERE ${fld} = #{val}

SELECT ROLE
  FROM USER_ROLES
 WHERE USERID = #{id}

mapper.delete:
DELETE FROM USER_ROLES
 WHERE USERID = #{id}
   AND ROLE = #{role}

mapper.update:
UPDATE USER_TBL
   SET ENABLED = #{enabled}
 WHERE ID = #{id}

mapper.select:
SELECT * FROM ( SELECT u.* ,FLOOR((ROWNUM - 1) / #{limit} + 1) PAGE FROM ( SELECT DISTINCT u3.* ,( SELECT LISTAGG(r2.ROLE, ',') WITHIN GROUP (ORDER BY u2.ID) FROM USER_TBL u2 ,USER_ROLES r2 WHERE r2.USERID = u2.ID AND r2.USERID = u3.ID ) AS "ROLE" FROM USER_TBL u3 ,USER_ROLES r3 WHERE u3.ID = r3.USERID
AND JOINDATE LIKE TO_DATE(#{searchWord}, 'YYYY-MM-DD')
ORDER BY ID ${ordering} ) u ) WHERE PAGE = #{page}
